import React, { Component } from 'react';
import '../Font/Font.css';
import '../FormElements/FormElements.css';
import "../Tables/Tables.css";
import { DEFAULTITEM } from "../Defaults";
import Result from "../CustomItem/Result";



class ServingWeight extends Component {

    static defaultProps = {
        item:  {DEFAULTITEM},
        weight: 0,
        result: 0
      }

  render() {

 const weight = this.props.weight
 const result = this.props.result.toFixed(0)
 .replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1,")


    return (
        <td className="weightH tooltip gram">
         {!weight? "custom" : weight}
          <span className="tooltiptext">grams</span>
                   <Result
                   result = {result}
                   />
                  </td>
   )}

}


export default ServingWeight
