  static defaultProps = {
    tripDates:  [""],
  }


  render() {

  const tripDates = this.props.tripDates
  const id = this.props.id


  const onSubmitForm = (e) => {
    e.preventDefault() 
    const quantInput =  e.target.quantInput.value

    this.props.handleSelectDay(quantInput,id)


  }


    return (
      <ul className= "scroll"> 
      {tripDates.map((date)=> (
  <li key= {date}>

  <label className="labelContainer"><Moment format= "MMM/DD">{date}</Moment>
       <input type="checkbox"

       />
       <span className="checkmark"></span>
   </label>
  </li>))}

        </ul>

     )


      }}


export default TripDates
