import React, { Component } from "react";
import "./Home.css";
import "../FormElements/FormElements.css";
import "../Font/Font.css";
import uYtitle from "../Images/uYtitle.png";
import { withRouter } from "react-router-dom";
import { AmplifyAuthenticator, AmplifySignOut } from '@aws-amplify/ui-react';
import { AuthState, onAuthUIStateChange } from '@aws-amplify/ui-components';
import awsconfig from '../aws-exports';

Amplify.configure(awsconfig);


class SignInBox extends Component {
  render() {


    const AuthStateApp = () => {
      const [authState, setAuthState] = React.useState();
      const [user, setUser] = React.useState();

      React.useEffect(() => {
          return onAuthUIStateChange((nextAuthState, authData) => {
              setAuthState(nextAuthState);
              setUser(authData)
          });
      }, []);

    return authState === AuthState.SignedIn && user ? (
        <div className="App">
            <div>Hello, {user.username}</div>
            <AmplifySignOut />
        </div>
      ) : (
        <AmplifyAuthenticator />
    );
  }




    return (
      <div className= "home">
        <div className= "home">
          <img className= "uYtitle" src={uYtitle} alt="ultraYumm logo" />
          <h1 className= "black montebello title">
            Get more energy!<span>&nbsp;&nbsp;</span>
            <span className="lighter">pack lighter{AuthStateApp()}</span>
          </h1>

        </div>


      </div>
    );
  }
}
export default withRouter(SignInBox);
