import React, { Component } from 'react';
import '../Font/Font.css';
import '../FormElements/FormElements.css';
import Moment from 'react-moment';




class TripDates extends Component {

  static defaultProps = {
    tripDates:  [""],
  }

  constructor(props) {
    super(props);
    this.state = {
    	checked: false
	}
  }


  render() {

  const name = this.props.name

  const defaultDay = this.props.defaultDay

  const tripDates = this.props.tripDates
  const id = this.props.id
  function onlyOne(checkbox) {
  const checkboxes =  document.getElementsByName('check')
    checkboxes.forEach((item) => {
        if (item !== checkbox) item.checked = false

    })
}



    return (

      <ul className= "scroll"> 
      {tripDates.map((date, key)=> (
          <li key= {date}>
          <label className="labelContainer">
            {name === ""? "" :<Moment format= "MMM/DD">{date === defaultDay? "" : date}</Moment>}
              <input type="checkbox"
              name= "check"
              defaultValue = {date}
              onClick={() => {
                            const selectedDay = date
                            onlyOne(this)
                            this.props.handleSelectDay(selectedDay, id)
                            this.setState ({
                              checked: true,
                              date: selectedDay
                            })

                          }}
              />
              <span 
              date = {date} 
              className= {"checkmark " + (this.state.selectedDay === date? "checked" : "notChecked")}></span>
          </label>
          </li>))}

        </ul>

     )


      }}


export default TripDates
