import React, { Component } from 'react';
import '../Font/Font.css';
import '../FormElements/FormElements.css';



class Quant extends Component {

  static defaultProps = {
    input:  1,
    result: 1
  }



  render() {

    const input = this.props.input
    const result = this.props.result.toFixed(0)
    .replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1,")
    const id = this.props.id

    const onSubmitForm = (e) => {
      e.preventDefault()

      const quantInput =  e.target.quantInput.value
      this.props.handleAdjustQuant(quantInput, id)
    }

    return (
      <form className= "quant" onSubmit={onSubmitForm}>

      <input className="tableAdjust dataResult sinkBackground"
      placeholder = {!result? input : result}
      name= 'quantInput'
      >
    </input>
    </form>

   )}

}


export default Quant
