import React, { Component } from 'react';
import '../Font/Font.css';
import '../FormElements/FormElements.css';



class UnitQuant extends Component {

  static defaultProps = {
    input:  1,
  }

  constructor(props) {
    super(props);
    this.state = {
      serving_qty: 1}}



  render() {

      const input = this.props.input



      const buildHandleKeyUp = setter => (e) => {

      e.preventDefault() 
      const input = e.currentTarget.value 
      setter(input);
      this.props.handleAdjustQuant(input)

      }


    return (
      <input className="dataResult skinBackground"
      type = "number"
      min= "0"
      name="itemQuantity"
      default Value = {isNaN(input)? 1 : input}

      onKeyUp = {buildHandleKeyUp((value) => {
        this.setState(
          {serving_qty: value} 
        )


      })}
      />


   )}

}


export default UnitQuant
