import React, { Component } from 'react';
import '../Font/Font.css';
import '../FormElements/FormElements.css';



class TripNames extends Component {


  render() {

    const trips = this.props.trips

    function onlyOne(checkbox) {
      var checkboxes = document.getElementsByName('check')
      checkboxes.forEach((item) => {
          if (item !== checkbox) item.checked = false
      })
  }

    return (
        <div className= "filterSelection"> 
            <ul className= "scroll"> 
            {trips.map((trips, key)=> (
              <li key= {key}>
              <label className= "labelContainer"> {trips.name}
                   <input 
                   name= "check"
                   type= "checkbox"
                   onClick={() => {
                    const selectTripId = trips.id
                    const tripName = trips.name
                    const trip = trips
                    onlyOne(this)
                    this.props.handleSelectTrip(selectTripId, tripName, trip)

                  }}

                   />
                   <span className= "checkmark"></span>
               </label>
              </li>
            ))}   
            </ul>
       </div>
     )}

}


export default TripNames
