import React, { Component } from 'react';
import '../Font/Font.css';
import '../FormElements/FormElements.css';
import { ITEMTYPES } from "../Defaults";






class ItemTypes extends Component {

  static defaultProps = {
    itemTypes: ITEMTYPES,
    stateType: "TBD"
  }




  render() {

    const stateType = this.props.stateType



    const itemTypes = this.props.itemTypes
    const itemTypesArray = itemTypes[0].type

    function onlyOne(checkbox) {
    const checkboxes =  document.getElementsByName('check')
      checkboxes.forEach((item) => {
          if (item !== checkbox) item.checked = false
      })
  }
      return (

          <ul className= "scroll">
          {itemTypesArray.map((type, key)=> (
            <li key= {key}>

            <label className={ stateType === {type} ? ' checked' : ' labelContainer ' }> {type}
                 <input type="checkbox"


                 defaultValue = {type}

                  name= "check"
                  onClick={() => {
                    const selectedType = type
                    console.log(selectedType)
                    onlyOne(this)
                    this.props.handleSelectType(selectedType)

                  }}

                 />
          <span className= "checkmark"></span>
             </label>
            </li>
          ))}        
          </ul>

     )}

}


export default ItemTypes
