import React, { Component } from 'react';
import '../Font/Font.css';
import '../FormElements/FormElements.css';
import "../Tables/Tables.css";
import { DEFAULTITEM } from "../Defaults";
import Quant from "../CustomItem/Quant";



class ServingQuant extends Component {

    static defaultProps = {
        item:  {DEFAULTITEM},
        quant: null
      }







  render() {

 const quant = this.props.quant

  const onSubmitForm = (e) => {
  e.preventDefault()

  const inputValue =  e.target.inputValue.value
  this.props.handleAdjustQuant(inputValue)
}

const placeholder = inputValue.toString()

    return (
        <td className="servingH">
          {quant}
          <p className="tableAdjust" handleAdjustQuant = {(input) =>
              this.adjustQuant(input)
            }
            >
            <form className = "quant" onSubmit={onSubmitForm}>
        <input type="text" 
        className = "adjust" min="0" step="0.5"
        name= 'inputValue'
        placeholder = {placeholder}

             />
        </form>

            </p>
        </td>


   )}

}


export default ServingQuant
